import { IEntity, IManager } from 'engine_api'
import Direction from '../component/Direction'
import SystemBase from './core/SystemBase'

const MOUSE_MOVE_EVENT_TYPE = 'mousemove'

export class MouseInput extends SystemBase {
  private _direction!: Direction

  constructor(entityManager: IManager<IEntity>) {
    super(entityManager)
    this.initializeMouseInput()
  }

  start(): void {
    this._direction = this._entity.getComponentStrict(Direction)
  }

  private initializeMouseInput() {
    document.addEventListener(
      MOUSE_MOVE_EVENT_TYPE,
      this.handleMouseMove.bind(this)
    )
  }

  private handleMouseMove(event: MouseEvent) {
    const mouseX = event.clientX
    const mouseY = event.clientY

    const directionX = mouseX - window.innerWidth / 2
    const directionY = mouseY - window.innerHeight / 2

    this._direction.direction.x = directionX
    this._direction.direction.y = directionY
  }

  unsubscribe() {
    document.removeEventListener(
      MOUSE_MOVE_EVENT_TYPE,
      this.handleMouseMove.bind(this)
    )
  }
}
