import {
  IInputManager,
  IEventSystem,
  IEntity,
  ISubscribeEntity,
  IEntityManager,
  IKeyboardEventData,
  KeyboardKeys,
  KeyActionWithParamMap,
  KeyboardEvent,
  KeyEvents,
} from 'engine_api'
import SystemBase from '../core/SystemBase'

export default class KeyboardMapping
  extends SystemBase
  implements ISubscribeEntity
{
  private _keyActions: KeyActionWithParamMap<string>
  private _keyDownCallback!: (key: string) => void

  constructor(
    entityManager: IEntityManager,
    private readonly _inputManager: IInputManager,
    private readonly _eventSystem: IEventSystem
  ) {
    super(entityManager)
    this._keyActions = this.initKeyAction()
  }

  private initKeyAction() {
    return {
      q: (id) =>
        this._eventSystem.publish(KeyboardEvent, {
          entityId: id,
          key: KeyboardKeys.q,
        } as IKeyboardEventData),
      e: (id) =>
        this._eventSystem.publish(KeyboardEvent, {
          entityId: id,
          key: KeyboardKeys.e,
        } as IKeyboardEventData),
      z: (id) =>
        this._eventSystem.publish(KeyboardEvent, {
          entityId: id,
          key: KeyboardKeys.z,
        } as IKeyboardEventData),
      x: (id) =>
        this._eventSystem.publish(KeyboardEvent, {
          entityId: id,
          key: KeyboardKeys.x,
        } as IKeyboardEventData),
    } as KeyActionWithParamMap<string>
  }

  start(): void {
    this.subscribe(this._entity)
  }

  subscribe(entity: IEntity): void {
    this._keyDownCallback = (inputKey: string) => {
      const key = inputKey.toLowerCase()
      if (this.isKeyMapped(key)) return
      this._keyActions[key](entity.id)
    }

    this._inputManager.subscribeInputEvent(
      KeyEvents.KeyDown,
      this._keyDownCallback
    )
  }

  unsubscribe() {
    this._inputManager.unsubscribeInputEvent(
      KeyEvents.KeyDown,
      this._keyDownCallback
    )
  }

  private isKeyMapped(key: string) {
    return !this._keyActions.hasOwnProperty(key)
  }
}
