import { ICollision, ICollisionAlgorithm, IRect, IVector2 } from 'engine_api'
import Vector2 from '../../../../math/vector/Vector2'

export default class CenterCollision implements ICollisionAlgorithm {
  private _rect1!: IRect
  private _rect2!: IRect
  private _collision!: ICollision
  private _point1!: IVector2
  private _point2!: IVector2

  start(collision: ICollision) {
    this._collision = collision
    const { position: position1 } = collision.object1.transform
    const { position: position2 } = collision.object2.transform
    const {
      halfSize: halfSize1,
      center: center1,
      size: size1,
    } = collision.object1.collider
    const {
      halfSize: halfSize2,
      center: center2,
      size: size2,
    } = collision.object2.collider

    this._point1 = new Vector2(center1.x - halfSize1.x, center1.y - halfSize1.y)
    this._point2 = new Vector2(center2.x - halfSize2.x, center2.y - halfSize2.y)
    this._rect1 = this.createRect(position1, this._point1, size1)
    this._rect2 = this.createRect(position2, this._point2, size2)
  }

  private createRect(
    position: IVector2,
    point: IVector2,
    size: IVector2
  ): IRect {
    const rect: IRect = {} as IRect

    rect.position = new Vector2(position.x + point.x, position.y + point.y)

    rect.size = new Vector2(size.x, size.y)

    return rect
  }

  private updatePoints() {
    const p1 = this._collision.object1.transform.position
    const p2 = this._collision.object2.transform.position

    this.updatePosition(this._rect1, p1, this._point1)
    this.updatePosition(this._rect2, p2, this._point2)
  }

  private updatePosition(rect: IRect, pos: IVector2, point: IVector2) {
    rect.position.x = pos.x + point.x
    rect.position.y = pos.y + point.y
  }

  isColliding(): boolean {
    this.updatePoints()
    return (
      this._rect1.position.x < this._rect2.position.x + this._rect2.size.x &&
      this._rect1.position.x + this._rect1.size.x > this._rect2.position.x &&
      this._rect1.position.y < this._rect2.position.y + this._rect2.size.y &&
      this._rect1.position.y + this._rect1.size.y > this._rect2.position.y
    )
  }
}
