import { ICollision, ICollisionAlgorithm, ICollisionCallback } from 'engine_api'

export default class CollisionDetector {
  private _collisionCallbacks: Map<string, ICollisionCallback> = new Map()
  private _noCollisionCallbacks: Map<string, ICollisionCallback> = new Map()
  private _collision!: ICollision
  private _key!: string

  constructor(private readonly _collisionAlgorithm: ICollisionAlgorithm) {}

  start(collision: ICollision): void {
    this._collision = collision
    this._collisionAlgorithm.start(collision)
    this._key = this.getCollisionKey()
  }

  private getCollisionKey(): string {
    const id1 = this._collision.object1.entityId
    const cid1 = this._collision.object1.collider.id
    const id2 = this._collision.object2.entityId
    return `${id1}_${cid1}_${id2}`
  }

  update() {
    const isColliding = this._collisionAlgorithm.isColliding()

    if (isColliding) {
      const callback = this._collisionCallbacks.get(this._key)
      if (callback) {
        callback(this._collision)
      }
    } else {
      const callback = this._noCollisionCallbacks.get(this._key)
      if (callback) {
        callback(this._collision)
      }
    }
  }

  subscribe(
    collisionCallback: ICollisionCallback,
    noCollisionCallback: ICollisionCallback
  ): void {
    this._collisionCallbacks.set(this._key, collisionCallback)
    this._noCollisionCallbacks.set(this._key, noCollisionCallback)
  }
}
