  isColliding(): boolean {
    const p1 = this._rect1.position
    const p2 = this._rect2.position
    const s1 = this._rect1.size
    const s2 = this._rect2.size
    return (
      p1.x < p2.x + s2.x &&
      p1.x + s1.x > p2.x &&
      p1.y < p2.y + s2.y &&
      p1.y + s1.y > p2.y
    )
  }
