import {
  ICollisionHandler,
  IEntityManager,
  IEntity,
  ICollision,
  ICollisionEntity,
} from 'engine_api'
import BoxCollider from '../../component/BoxCollider'
import CollisionObject from '../../component/CollisionObject'
import RigidBody from '../../component/RigidBody'
import Transform from '../../component/Transform'
import SystemBase from '../core/SystemBase'
import CollisionDetector from './detector/CollisionDetector'

export default class CollisionSystem extends SystemBase {
  protected _collision: ICollision = {
    object1: {} as ICollisionEntity,
    object2: {} as ICollisionEntity,
  }
  protected _entity2!: IEntity
  protected _boxColliderId: string = ''

  constructor(
    entityManager: IEntityManager,
    private readonly _collisionDetector: CollisionDetector,
    private readonly _collisionHandler: ICollisionHandler
  ) {
    super(entityManager)
  }

  start() {
    this.setBoxColliderId()
    this.setObject1()
    this._entity2 = this.setEntity2()
    this.setObject2()
    this._collisionDetector.start(this._collision)
    this._collisionDetector.subscribe(
      this._collisionHandler.onCollisionHandler.bind(this._collisionHandler),
      this._collisionHandler.offCollisionHandler.bind(this._collisionHandler)
    )
  }

  protected setBoxColliderId() {}

  private setObject1() {
    this._collision.object1 = {
      entityId: this._entity.id,
      collider:
        this._boxColliderId === ''
          ? this._entity.getComponentStrict(BoxCollider)
          : this._entity.getComponentStrict(BoxCollider, this._boxColliderId),
      rigidBody: this._entity.getComponentStrict(RigidBody),
      transform: this._entity.getComponentStrict(Transform),
    }
  }

  private setEntity2() {
    return this._entityManager.getStrict(
      this._entity.getComponentStrict(CollisionObject).objectIdToCollideWith
    )
  }

  private setObject2() {
    this._collision.object2 = {
      entityId: this._entity2.id,
      collider: this._entity2.getComponentStrict(BoxCollider),
      rigidBody: this._entity2.getComponentStrict(RigidBody),
      transform: this._entity2.getComponentStrict(Transform),
    }
  }

  update(_deltaTime: number): void {
    this._collisionDetector.update()
  }
}
