import Transform from '../../component/Transform'
import RigidBody from '../../component/RigidBody'
import CircleCollider from '../../component/CircleCollider'
import InsideBoxCollider from '../../component/InsideBoxCollider'
import SystemBase from '../core/SystemBase'

enum Axis {
  X = 'x',
  Y = 'y',
}

export default class InsideBoxCollisionSystem extends SystemBase {
  private _rigidBody!: RigidBody
  private _transform!: Transform
  private _circleCollider!: CircleCollider
  private _insideBoxCollider!: InsideBoxCollider

  start(): void {
    this._rigidBody = this._entity.getComponentStrict(RigidBody)
    this._transform = this._entity.getComponentStrict(Transform)
    this._circleCollider = this._entity.getComponentStrict(CircleCollider)
    this._insideBoxCollider = this._entity.getComponentStrict(InsideBoxCollider)
  }

  update(_deltaTime: number) {
    ;[Axis.X, Axis.Y].forEach((axis: any) => {
      this.checkAndHandleCollision(axis)
    })
  }

  private checkAndHandleCollision(axis: Axis) {
    const position = this._transform.position[axis]
    const radius = this._circleCollider.radius
    const wallPosition = this._insideBoxCollider.size[axis]

    if (this.isCollisionDetected(position, radius, wallPosition)) {
      this.resolveCollision(radius, wallPosition, axis)
    }
  }

  private isCollisionDetected(
    position: number,
    radius: number,
    maxlimit: number,
    minLimit: number = 0
  ): boolean {
    return position - radius <= minLimit || position + radius >= maxlimit
  }

  private resolveCollision(radius: number, maxLimit: number, axis: Axis) {
    this._rigidBody.velocity[axis] *= -1
    this._transform.position[axis] = this.adjustPosition(
      this._transform.position[axis],
      radius,
      maxLimit
    )
  }

  private adjustPosition(
    position: number,
    radius: number,
    limit: number
  ): number {
    return Math.max(radius, Math.min(limit - radius, position))
  }
}
