import LimitMove from '../../component/LimitMove'
import Transform from '../../component/Transform'
import BoxCollider from '../../component/BoxCollider'
import SystemBase from '../core/SystemBase'

export default class LimitMoveSystem extends SystemBase {
  private _limitMove!: LimitMove
  private _boxCollider!: BoxCollider
  private _transform!: Transform

  start(): void {
    this._limitMove = this._entity.getComponentStrict(LimitMove)
    this._boxCollider = this._entity.getComponentStrict(BoxCollider, 'body')
    this._transform = this._entity.getComponentStrict(Transform)
  }

  update(_deltaTime: number) {
    const { x: offsetX, y: offsetY } = this._boxCollider.center

    const halfWidth = this._boxCollider.halfSize.x
    const halfHeight = this._boxCollider.halfSize.y

    const newX = Math.max(
      halfWidth + offsetX,
      Math.min(
        this._limitMove.limitSize.x - halfWidth - offsetX,
        this._transform.position.x
      )
    )
    const newY = Math.max(
      halfHeight - offsetY,
      Math.min(
        this._limitMove.limitSize.y - halfHeight - offsetY,
        this._transform.position.y
      )
    )

    this._transform.position.x = newX
    this._transform.position.y = newY
  }
}
