import {
  ICollisionHandler,
  IEntityManager,
  IManager,
  IEntityDataModel,
  IEventSystem,
  ICollisionInfo,
  IScoreInfo,
} from 'engine_api'
import {
  Transform,
  RigidBody,
  Vector2,
  componentType,
  EventNames,
  GameState,
  IdleState,
  EntityId,
} from 'engine'

export default class BallGateCollisionHandler implements ICollisionHandler {
  constructor(
    private readonly _entityManager: IEntityManager,
    private readonly _entityDataManager: IManager<IEntityDataModel>,
    private readonly _eventSystem: IEventSystem
  ) {}

  handleCollision(collisionInfo: ICollisionInfo) {
    this.resetEntities()
    this.updateEntitiesPositions()
    this.handleGateCollisions(collisionInfo)
  }

  handleNoCollision(collisionInfo: ICollisionInfo) {}

  private resetEntities() {
    this.resetEntity(EntityId.Player1)
    this.resetEntity(EntityId.Player2)
    this.resetEntity(EntityId.Ball)
  }

  private resetEntity(entityId: string) {
    const entityData = this._entityDataManager.getStrict(entityId)
    const entity = this._entityManager.getStrict(entityId)
    const transform = entity.getComponentStrict(Transform)
    const rigidBody = entity.getComponentStrict(RigidBody)

    rigidBody.velocity = new Vector2()
    transform.position = Vector2.getNew(
      entityData[componentType.Transform].position
    )
  }

  private updateEntitiesPositions() {
    this.updateEntityPosition(EntityId.Player1)
    this.updateEntityPosition(EntityId.Player2)
    this.updateEntityPosition(EntityId.Ball)
  }

  private updateEntityPosition(entityId: string) {
    const entityData = this._entityDataManager.getStrict(entityId)
    const entity = this._entityManager.getStrict(entityId)
    const transform = entity.getComponentStrict(Transform)

    transform.position = Vector2.getNew(
      entityData[componentType.Transform].position
    )
  }

  private handleGateCollisions(collisionInfo: ICollisionInfo) {
    const gameState = this._entityManager
      .getStrict(EntityId.GameState)
      .getComponentStrict(GameState)

    if (collisionInfo.object1.entityId === EntityId.LeftGate) {
      this.handleLeftGateCollision(gameState)
    }

    if (collisionInfo.object1.entityId === EntityId.RightGate) {
      this.handleRightGateCollision(gameState)
    }
  }

  private handleLeftGateCollision(gameState: GameState) {
    gameState.score.blueScore++
    this.publishScore(gameState)
    this.publishIdle()
  }

  private handleRightGateCollision(gameState: GameState) {
    gameState.score.redScore++
    this.publishScore(gameState)
    this.publishIdle()
  }

  private publishScore(gameState: GameState) {
    this._eventSystem.publish(EventNames.UpdateScore, {
      redScore: gameState.score.redScore,
      blueScore: gameState.score.blueScore,
    } as IScoreInfo)
  }

  private publishIdle() {
    this._eventSystem.publish(EventNames.ChangeState, {
      id: EntityId.Ball,
      newState: new IdleState(this._eventSystem),
    })
  }
