import { EventNames, MoveState } from 'engine'
import { ICollisionHandler, ICollisionInfo, IEventSystem } from 'engine_api'

export default class PlayerBootsBallCollisionHandler
  implements ICollisionHandler
{
  constructor(private readonly _eventSystem: IEventSystem) {}

  handleCollision(collisionInfo: ICollisionInfo) {
    const obj1 = collisionInfo.object1.rigidBody
    const obj2 = collisionInfo.object2.rigidBody

    const relativeV = obj2.velocity.clone().subtract(obj1.velocity)

    const obj2MassFactor = (2 * obj1.mass) / (obj1.mass + obj2.mass)

    const newObj2Velocity = obj2.velocity
      .clone()
      .subtract(relativeV.clone())
      .multiply(obj2MassFactor)
      .multiply(obj2.cor)

    obj2.velocity
      .setValues(newObj2Velocity)
      .multiply(obj2.speedMultiplier)
      .clampLength(0, obj2.maxSpeed)

    this._eventSystem.publish(EventNames.StoreLastCollisionInfo, collisionInfo)
    if (Math.abs(obj2.velocity.x) > 0 || Math.abs(obj2.velocity.y) > 0)
      this._eventSystem.publish(EventNames.ChangeState, {
        id: collisionInfo.object2.entityId,
        newState: new MoveState(this._eventSystem),
      })
  }

  handleNoCollision(collisionInfo: ICollisionInfo) {}
}
