import GameState from '../component/GameState'
import Vector2 from '../../math/vector/Vector2'
import Map from '../component/Map'
import { EventNames } from '../../event_system/EventNames'
import Sprite from '../component/Sprite'
import Transform from '../component/Transform'
import BoxCollider from '../component/BoxCollider'
import RigidBody from '../component/RigidBody'
import Direction from '../component/Direction'

export default class RenderSubSystem implements IRenderSubSystem {
  private _offset1: IVector2 = new Vector2(20, 0)
  private _offset2: IVector2 = new Vector2(15, 0)

  constructor(
    private readonly _renderer: IRendererV2,
    private readonly _spriteAnimatorManager: IManager<SpriteAnimator>,
    private readonly _tileMap: IRenderable,
    private readonly _eventSystem: IEventSystem
  ) {}

  addSprite(entity: IEntity): void {
    const sprite = entity.getComponentStrict(Sprite)

    this._spriteAnimatorManager.add(
      entity.id,
      new SpriteAnimator(sprite.spriteAnimation)
    )

    this._eventSystem.subscribe(
      EventNames.AnimationSwitch,
      (data: { id: string; animId: number }) => {
        this._spriteAnimatorManager
          .getStrict(data.id)
          .switchAnimation(data.animId)
      }
    )
  }

  renderLogic(deltaTime: number, entity: IEntity) {
    const sprite = entity.getComponent(Sprite)
    const transform = entity.getComponent(Transform)
    const boxCollider = entity.getComponent(BoxCollider)
    const body = entity.getComponent(BoxCollider, 'body')
    const boots = entity.getComponent(BoxCollider, 'boots')
    const direction = entity.getComponent(Direction)
    const rigidBody = entity.getComponent(RigidBody)
    const gameState = entity.getComponent(GameState)
    const map = entity.getComponent(Map)

    if (sprite && transform) {
      if (sprite.spriteAnimation.length > 0) {
        const animator = this._spriteAnimatorManager.getStrict(entity.id)
        animator.update(deltaTime)
        animator.draw(
          this._renderer.ctx,
          transform.position.x + sprite.spriteOffset.x,
          transform.position.y + sprite.spriteOffset.y,
          sprite.isFlipped
        )
      }
    }

    if (
      boxCollider &&
      boxCollider.enableRender &&
      transform &&
      rigidBody &&
      direction
    ) {
      this._renderer.drawObject(transform, boxCollider, rigidBody, direction)
    }

    if (body && body.enableRender && transform) {
      this._renderer.drawCollisionBox(transform, body)
    }
    if (boots && boots.enableRender && transform) {
      this._renderer.drawCollisionBox(transform, boots)
    }

    if (gameState) {
      this._renderer.drawText(':', gameState.position, 'yellow', '20px Arial')

      this._renderer.drawText(
        gameState.score.redScore.toString(),
        new Vector2(
          gameState.position.x - this._offset1.x,
          gameState.position.y
        ),
        'red',
        '20px Arial'
      )

      this._renderer.drawText(
        gameState.score.blueScore.toString(),
        new Vector2(
          gameState.position.x + this._offset2.x,
          gameState.position.y
        ),
        'blue',
        '20px Arial'
      )
    }

    if (map) {
      this._tileMap.render(deltaTime)
    }
  }
}
