import ClientMovement from '../../component/ClientMovement'
import ServerMovement from '../../component/ServerMovement'
import { componentType } from '../../component/core/componentType'
import IComponentMapper from '../IComponentMapper'
import ComponentMapper from '../ComponentMapper'

export default class EntityBuilder implements IEntityBuilder {
  protected _entity!: IEntity
  private _entityData!: IEntityDataModel
  private _operationMap: OperationMap = new OperationMap()
  private _assert: AssertHelper = new AssertHelper(this)
  private _mapper: IComponentMapper = new ComponentMapper()

  get entityData(): IEntityDataModel {
    this.assertEntityData()
    return this._entityData
  }

  constructor(
    private readonly _dataManager: IManager<IEntityDataModel>,
    private readonly _entityManager: IEntityManager
  ) {}

  recordOperation(operation: Operation): this {
    this._operationMap.addOperation(operation)
    return this
  }

  protected executeOperations(entityDatakey: string): void {
    this._operationMap.executeOperations(entityDatakey)
  }

  private assertEntity(): void {
    this._assert.assertField('_entity')
  }

  private assertEntityData(): void {
    this._assert.assertField('_entityData')
  }

  withEntity<T extends IEntity>(entityFunction: () => T) {
    this._entity = entityFunction()
    return this
  }

  withMap(): this {
    this._entity.addComponent(new Map())
    return this
  }

  withEntityData(entityDataKey: string): this {
    this.assertEntity()
    this._entityData = this._dataManager.getStrict(entityDataKey)
    this._entity.id = this._entityData.id
    return this
  }

  withSprite(): this {
    this.addComponent(this.map<ISprite>(componentType.Sprite))
    return this
  }

  withClientMovement(): this {
    this._entity.addComponent(new ClientMovement())
    return this
  }

  withServerMovement(): this {
    this._entity.addComponent(new ServerMovement())
    return this
  }

  withCollisionObject() {
    this.addComponent(this.map<ICollisionObject>(componentType.CollisionObject))
    return this
  }

  withLimitMove() {
    this.addComponent(this.map<ILimitMove>(componentType.LimitMove))
    return this
  }

  withGameState() {
    this.addComponent(this.map<IGameState>(componentType.GameState))
    return this
  }

  withCircleCollider() {
    this.addComponent(this.map<ICircleCollider>(componentType.CircleCollider))
    return this
  }

  withInsideBoxCollider() {
    this.addComponent(this.map<IBoxCollider>(componentType.InsideBoxCollider))
    return this
  }

  private map<TComponent>(componentType: string): TComponent | TComponent[] {
    return this._mapper.mappers[componentType](
      this.getData<TComponent>(componentType)
    ) as TComponent
  }

  private getData<TData>(componentType: string) {
    this.assertEntityData()
    const data = this._entityData[componentType]
    if (Array.isArray(data)) {
      return data as TData[]
    } else {
      return data as TData
    }
  }

  withTransform() {
    this.addComponent(this.map<ITransform>(componentType.Transform))
    return this
  }

  withBoxCollider() {
    this.addComponent(this.map<IBoxCollider>(componentType.BoxCollider))
    return this
  }

  private addComponent(component: IComponent | IComponent[]) {
    if (Array.isArray(component)) {
      this._entity.addComponents(component)
    } else {
      this._entity.addComponent(component)
    }
  }

  withRigidBody() {
    this.addComponent(this.map<IRigidBody>(componentType.RigidBody))
    return this
  }

  build(entityDataKey: string, entityKey: string) {
    this.executeOperations(entityDataKey)
    this.assertEntity()
    this._entityManager.add(entityKey, this._entity)
  }
}
