import {
  ClassType,
  IComponent,
  IEntity,
  IEntityWithComponents,
} from 'engine_api'

export default class Entity implements IEntityWithComponents, IEntity {
  private _components = new Map<ClassType<IComponent>, IComponent[]>()
  private _id!: string

  get id(): string {
    return this._id
  }

  set id(id: string) {
    this._id = id
  }

  addComponent(component: IComponent): void {
    this.addComponentToEntity(component)
  }

  addComponents(components: IComponent[]): void {
    for (const component of components) {
      this.addComponentToEntity(component)
    }
  }

  private addComponentToEntity(component: IComponent) {
    const componentType = component.constructor as ClassType<IComponent>

    if (!this._components.has(componentType)) {
      this._components.set(componentType, [component])
    } else {
      this._components.get(componentType)!.push(component)
    }
  }

  getComponent<T extends IComponent>(
    componentType: ClassType<T>,
    id?: string
  ): T | undefined {
    const components = this._components.get(componentType)

    if (components) {
      if (id) {
        return components.find((item) => item.id === id) as T | undefined
      } else {
        return components[0] as T | undefined
      }
    }

    return undefined
  }

  getComponentStrict<T extends IComponent>(
    componentType: ClassType<T>,
    id?: string
  ): T {
    const components = this._components.get(componentType)
    const errorMessage = (id?: string) =>
      id
        ? `Component of type ${componentType.name} and id ${id} not found.`
        : `No components of type ${componentType.name} found.`
    if (components) {
      if (id) {
        const component = components.find((item) => item.id === id) as
          | T
          | undefined

        if (component) {
          return component
        } else {
          throw new Error(errorMessage(id))
        }
      } else {
        const firstComponent = components[0] as T | undefined

        if (firstComponent) {
          return firstComponent
        } else {
          throw new Error(errorMessage())
        }
      }
    } else {
      throw new Error(errorMessage())
    }
  }

  update(deltaTime: number): void {
    for (const componentArrays of this._components.values()) {
      for (const component of componentArrays) {
        component.update(deltaTime)
      }
    }
  }

  render(deltaTime: number): void {
    for (const componentArrays of this._components.values()) {
      for (const component of componentArrays) {
        component.render(deltaTime)
      }
    }
  }
}
