import { IEntityManager, IEntity, IManager, IFactory } from 'engine_api'
import InitLogicSystemBase from '../base/init_logic/InitLogicSystemBase'
import IKeyboardMappingSubSystem from './IKeyboardMappingSubSystem'
import MapManager from '../../entity/manager/MapManager'

export default class KeyboardMappingSystem extends InitLogicSystemBase {
  private _subSystemManager: IManager<IKeyboardMappingSubSystem> =
    new MapManager<IKeyboardMappingSubSystem>()

  constructor(
    entityManager: IEntityManager,
    private readonly _subSystemFactory: IFactory<IKeyboardMappingSubSystem>
  ) {
    super(entityManager)
  }

  initLogic(entity: IEntity): void {
    const subSystem = this._subSystemFactory.create()
    subSystem.subscribeInput(entity)
    this._subSystemManager.add(entity.id, subSystem)
  }

  unsubscribe(entity: IEntity) {
    const subSystem = this._subSystemManager.getStrict(entity.id)
    subSystem.unsubscribeInput()
  }
}
