import { Entity, MapManager } from 'engine'
import { IEntity } from 'engine_api'
import IComponentMapper from './IComponentMapper'
import { componentList } from './componentList'

export default class EntityComponentCreator {
  constructor(private readonly _componentBuilder: IComponentMapper) {}

  createEntityManager(data: { [key: string]: Record<string, any> }) {
    const entityManager = new MapManager<IEntity>()
    for (const key in data) {
      const entity = new Entity()
      entity.id = key
      entityManager.add(key, entity)
      this.createEntityComponents(data[key], entity)
    }
    return entityManager
  }

  private createEntityComponents(data: Record<string, any>, entity: IEntity) {
    for (const key in data) {
      const componentType = componentList[key]
      if (componentType) {
        const componentBuild = this._componentBuilder.builders[componentType](
          data[key]
        )
        if (componentBuild) entity.addComponent(componentBuild)
        else console.warn(`Unsupported builder for component type: ${key}`)
      } else console.warn(`Unsupported component type: ${key}`)
    }
  }
}
