import ComponentMapper from './ComponentMapper'
import DataPrinter from './DataPrinter'
import EntityComponentCreator from './EntityComponentCreator'
import FileLoader from './FileLoader'

export default class EntityManagerInitializer {
  private isDataPrintOn: boolean
  private fileLoader: FileLoader
  private data: any // Assuming the type of data, adjust as needed
  private printer: DataPrinter
  private entityManager: any // Adjust the type according to your EntityManager

  constructor(isDataPrintOn: boolean) {
    this.isDataPrintOn = isDataPrintOn
    this.fileLoader = new FileLoader('./data/entityFormat.json')
    this.data = null
    this.printer = new DataPrinter()
    this.entityManager = null
  }

  async initialize(): Promise<void> {
    this.data = await this.fileLoader.getData()

    if (this.isDataPrintOn) {
      this.printData()
      this.printKeysAndValues()
      this.printValues()
    }

    this.initializeEntityManager()
    this.printEntity()
  }

  private printData(): void {
    this.printer.printData(this.data)
  }

  private printKeysAndValues(): void {
    this.printer.printKeysAndValues(this.data)
  }

  private printValues(): void {
    this.printer.printValues(this.data)
  }

  private initializeEntityManager(): void {
    const entityComponentCreator = new EntityComponentCreator(
      new ComponentMapper()
    )
    this.entityManager = entityComponentCreator.createEntityManager(this.data)
  }

  private printEntity(): void {
    this.printer.printEntity(this.entityManager)
  }
}

export async function testEntityBuildingFromJsonData() {
  const isDataPrintOn: boolean = false
  const entityManagerInitializer = new EntityManagerInitializer(isDataPrintOn)
  await entityManagerInitializer.initialize()
}
